import { useQuery } from "@tanstack/react-query";
import { Card, Divider, Image, Spin } from "antd";
import AxiosSecure from "../../Hook/useAxios";
import { UserType2 } from "../../Dashboard/Admin/AllTypes";

const GuestTimeline = ({ eventid }: { eventid: string | undefined }) => {
    const caxios = AxiosSecure();
    const queryAllGuest = useQuery({
        queryKey: ["allguest"],
        queryFn: async () => {
            const res = await caxios.get(`/guest?timelineid=${eventid}`);
            return res.data as { _id: string, guest: UserType2[] };
        },
        enabled: eventid != undefined,
        retry: 3,
        refetchOnWindowFocus: false,
    });

    return (
        <div >
            <div>
                
                <Divider className="my-2" />
                <div className="flex flex-col gap-2">
                    {queryAllGuest.isLoading || queryAllGuest.isRefetching ? (
                        <div className="flex justify-center">
                            <Spin size="large"></Spin>
                        </div>
                    ) : queryAllGuest.isSuccess ? (
                        queryAllGuest.data.guest.length != 0 ? (
                            queryAllGuest.data.guest.map((x, index) => {
                                return (
                                    <Card key={index}>
                                        <div className="flex justify-between items-center">
                                            <Image
                                                height={60}
                                                width={60}
                                                src={x.img_profile}
                                                className="rounded-full object-cover"
                                                fallback="data:image/png;base64,"
                                            />
                                            <Divider className="mx-2" type="vertical" />
                                            <p className="font-bold">{x.name}</p>
                                            <Divider className="mx-2" type="vertical" />
                                            <p className="text-xs font-semibold">{x.email}</p>
                                            <Divider className="mx-2" type="vertical" />
                                        </div>
                                    </Card>
                                );
                            })
                        ) : (
                            <p>No Guest Added</p>
                        )
                    ) : (
                        queryAllGuest.isError && <p>Something Error happended</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default GuestTimeline;
