import { useMutation, useQuery } from "@tanstack/react-query";
import { Button, Card, Divider, Image, Input, Spin } from "antd";
import { ChangeEvent } from "react";
import { MdOutlinePersonAddAlt } from "react-icons/md";
import AxiosSecure from "../../Hook/useAxios";
import { UserType2 } from "../../Dashboard/Admin/AllTypes";
import showToast from "../../Hook/swalToast";
import Swal from "sweetalert2";

const Guest = ({ eventid }: { eventid: string | undefined }) => {
  const caxios = AxiosSecure();
  const queryAllGuest = useQuery({
    queryKey: ["allguest"],
    queryFn: async () => {
      const res = await caxios.get(`/guest?timelineid=${eventid}`);
      return res.data as {_id:string,guest:UserType2[]};
    },
    enabled: eventid != undefined,
    retry: 3,
    refetchOnWindowFocus: false,
  });
  const searchGuest = useMutation({
    mutationFn: async (text: string) => {
      const res = await caxios.post(`/guest`, { text });
      return res.data as UserType2[];
    },
  });
  const mutaionAddGuest = useMutation({
    mutationFn: async (id: string) => {
      const res = await caxios.patch(`/addguest/${eventid}`, { id });
      return res.data;
    },
    onSuccess: () => {
      queryAllGuest.refetch();
    },
  });
  const mutationDeleteGuest=useMutation({
    mutationFn:async (index:number)=>{
        const res= await caxios.delete(`/guest/${eventid}?index=${index}`)
        return res.data
    },
    onSuccess:async ()=>{
        queryAllGuest.refetch()
    }
  })
  function deleteGuest(index:number) {
    const t_guest= queryAllGuest.data?.guest.at(index)
    Swal.fire({
        title:`Do you want to delete ${t_guest?.name}?`,
        icon:"question",
        showCancelButton:true,
    }).then(res=>{
        if (res.isConfirmed) {
            mutationDeleteGuest.mutate(index)
        }
    })
  }
  function addGuest(id: string | undefined) {
    if (id && queryAllGuest.data) {
      for (const guest of queryAllGuest.data.guest) {
        if (guest._id == id) {
          showToast("error", "Guest Exist");
          return;
        }
      }
      mutaionAddGuest.mutate(id);
    }
  }
  function onChange(event: ChangeEvent<HTMLInputElement>) {
    const value = event.currentTarget.value;
    if (value.length > 2) {
      searchGuest.mutateAsync(value);
    }
  }
  return (
    <div >
      <div>
        <Input onChange={onChange} />
        <p className="font-light italic text-gray-700 text-sm text-right">
          Search Guest using their name or email. And minimum 3 letter
        </p>
        <div className="flex gap-3 flex-wrap">
          {searchGuest.isPending ? (
            <div className="flex justify-center">
              <Spin size="large"></Spin>
            </div>
          ) : searchGuest.isSuccess ? (
            searchGuest.data.length != 0 ? (
              searchGuest.data.map((x, index) => {
                return (
                  <Card key={index} style={{ width: 180 }} className="relative">
                    <Image
                      height={120}
                      width={120}
                      src={x.img_profile}
                      className="rounded object-cover"
                      fallback="data:image/png;base64,"
                    />
                    <div className="flex flex-col justify-center h-full">
                      <p className="font-bold text-left">{x.name}</p>
                      <p className="text-xs font-semibold text-left mb-6">
                        {x.email}
                      </p>
                      <div
                        onClick={() => addGuest(x._id)}
                        className="absolute bottom-1 left-0 right-0 hover:text-blue-500 hover:cursor-pointer"
                      >
                        <Divider className="my-2" />
                        <MdOutlinePersonAddAlt
                          className="text-center w-full text-xl"
                          key="add"
                        />
                      </div>
                    </div>
                  </Card>
                );
              })
            ) : (
              <p>No Guest Found</p>
            )
          ) : (
            searchGuest.isError && <p>Something Error happended</p>
          )}
        </div>
        <Divider className="my-2" />
        <div className="flex flex-col gap-2">
          {queryAllGuest.isLoading || queryAllGuest.isRefetching ? (
            <div className="flex justify-center">
              <Spin size="large"></Spin>
            </div>
          ) : queryAllGuest.isSuccess ? (
            queryAllGuest.data.guest.length != 0 ? (
              queryAllGuest.data.guest.map((x, index) => {
                return (
                  <Card key={index}>
                    <div className="flex justify-between items-center">
                      <Image
                        height={60}
                        width={60}
                        src={x.img_profile}
                        className="rounded-full object-cover"
                        fallback="data:image/png;base64,"
                      />
                      <Divider className="mx-2" type="vertical" />
                      <p className="font-bold">{x.name}</p>
                      <Divider className="mx-2" type="vertical" />
                      <p className="text-xs font-semibold">{x.email}</p>
                      <Divider className="mx-2" type="vertical" />
                      <Button className="bg-red-500 text-white font-semibold" onClick={()=>{deleteGuest(index)}}>Delete</Button>
                    </div>
                  </Card>
                );
              })
            ) : (
              <p>No Guest Added</p>
            )
          ) : (
            queryAllGuest.isError && <p>Something Error happended</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Guest;
